#!/bin/python3

def bathroom_security (keys):
    position = [1, 1]
    keypad = [[1, 2, 3],[4, 5, 6],[7, 8, 9]]
    solution = ''
    for key in keys:
        for delta in key:
            if delta == "U": position[0] = max((position[0] - 1), 0)
            elif delta == "R": position[1] = min((position[1] + 1), 2)
            elif delta == "D": position[0] = min((position[0] + 1), 2)
            elif delta == "L": position[1] = max((position[1] - 1), 0)
        solution += str(keypad[position[0]][position[1]])
    return solution

sample_1 = ["ULL", "RRDDD", "LURDL", "UUUUD"]
print("Sample 1: {}".format(bathroom_security(sample_1)))

advent_input = ["",
"",
"",
"",
"",]
print("Challenge: {}".format(bathroom_security(advent_input)))